export default class EventTargetMixin{constructor(){this._listeners=new Map}addEventListener(type,callback){this._listeners.has(type)||this._listeners.set(type,new Set),this._listeners.get(type).add(callback)}removeEventListener(type,callback){this._listeners.has(type)&&this._listeners.get(type).delete(callback)}dispatchEvent(event){return!this._listeners.has(event.type)||(this._listeners.get(event.type).forEach(callback=>callback.call(this,event)),!event.defaultPrevented)}}